function Dossier_histoire_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('Dossier_histoire');
	if(!$arrPerm[1]){
		return false;
	}

	$data['titre_histoire'] = makeSafe($_REQUEST['titre_histoire']);
		if($data['titre_histoire'] == empty_lookup_value){ $data['titre_histoire'] = ''; }
	$data['resume'] = makeSafe($_REQUEST['resume']);
		if($data['resume'] == empty_lookup_value){ $data['resume'] = ''; }
	$data['creer_par'] = parseCode('<%%creatorUsername%%>', true);
	if($data['titre_histoire']== ''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Titre de l\'histoire': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	if($data['resume'] == '') $data['resume'] = "C\'est l\'histoire de  ...";
	if($data['resume']== ''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Resume': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}

	// hook: Dossier_histoire_before_insert
	if(function_exists('Dossier_histoire_before_insert')){
		$args=array();
		if(!Dossier_histoire_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `Dossier_histoire` set       `titre_histoire`=' . (($data['titre_histoire'] !== '' && $data['titre_histoire'] !== NULL) ? "'{$data['titre_histoire']}'" : 'NULL') . ', `resume`=' . (($data['resume'] !== '' && $data['resume'] !== NULL) ? "'{$data['resume']}'" : 'NULL') . ', `creer_par`=' . "'{$data['creer_par']}'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"Dossier_histoire_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// automatic auteur
	if($_REQUEST['filterer_auteur']){
		sql("update `Dossier_histoire` set `auteur`='" . makeSafe($_REQUEST['filterer_auteur']) . "' where `id2dossier`='" . makeSafe($recID, false) . "'", $eo);
	}

	// hook: Dossier_histoire_after_insert
	if(function_exists('Dossier_histoire_after_insert')){
		$res = sql("select * from `Dossier_histoire` where `id2dossier`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!Dossier_histoire_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	sql("insert ignore into membership_userrecords set tableName='Dossier_histoire', pkValue='" . makeSafe($recID, false) . "', memberID='" . makeSafe(getLoggedMemberID(), false) . "', dateAdded='" . time() . "', dateUpdated='" . time() . "', groupID='" . getLoggedGroupID() . "'", $eo);

	return $recID;
}

