function Dossier_histoire_update($selected_id){
	global $Translation;

	// mm: can member edit record?
	$arrPerm=getTablePermissions('Dossier_histoire');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='Dossier_histoire' and pkValue='".makeSafe($selected_id)."'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='Dossier_histoire' and pkValue='".makeSafe($selected_id)."'");
	if(($arrPerm[3]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[3]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[3]==3){ // allow update?
		// update allowed, so continue ...
	}else{
		return false;
	}

	$data['titre_histoire'] = makeSafe($_REQUEST['titre_histoire']);
		if($data['titre_histoire'] == empty_lookup_value){ $data['titre_histoire'] = ''; }
	if($data['titre_histoire']==''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Titre de l\'histoire': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	$data['resume'] = makeSafe($_REQUEST['resume']);
		if($data['resume'] == empty_lookup_value){ $data['resume'] = ''; }
	if($data['resume']==''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Resume': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	$data['selectedID']=makeSafe($selected_id);

	// hook: Dossier_histoire_before_update
	if(function_exists('Dossier_histoire_before_update')){
		$args=array();
		if(!Dossier_histoire_before_update($data, getMemberInfo(), $args)){ return false; }
	}

	$o=array('silentErrors' => true);
	sql('update `Dossier_histoire` set       `titre_histoire`=' . (($data['titre_histoire'] !== '' && $data['titre_histoire'] !== NULL) ? "'{$data['titre_histoire']}'" : 'NULL') . ', `resume`=' . (($data['resume'] !== '' && $data['resume'] !== NULL) ? "'{$data['resume']}'" : 'NULL') . " where `id2dossier`='".makeSafe($selected_id)."'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo '<a href="Dossier_histoire_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: Dossier_histoire_after_update
	if(function_exists('Dossier_histoire_after_update')){
		$res = sql("SELECT * FROM `Dossier_histoire` WHERE `id2dossier`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = $data['id2dossier'];
		$args = array();
		if(!Dossier_histoire_after_update($data, getMemberInfo(), $args)){ return; }
	}

	// mm: update ownership data
	sql("update membership_userrecords set dateUpdated='".time()."' where tableName='Dossier_histoire' and pkValue='".makeSafe($selected_id)."'", $eo);

}

function Dossier_histoire_form($selected_id = '', $AllowUpdate = 1, $AllowInsert = 1, $AllowDelete = 1, $ShowCancel = 0, $TemplateDV = '', $TemplateDVP = ''){
	// function to return an editable form for a table records
	// and fill it with data of record whose ID is $selected_id. If $selected_id
	// is empty, an empty form is shown, with only an 'Add New'
	// button displayed.

	global $Translation;

	// mm: get table permissions
	$arrPerm=getTablePermissions('Dossier_histoire');
	if(!$arrPerm[1] && $selected_id==''){ return ''; }
	$AllowInsert = ($arrPerm[1] ? true : false);
	// print preview?
	$dvprint = false;
	if($selected_id && $_REQUEST['dvprint_x'] != ''){
		$dvprint = true;
	}

	$filterer_auteur = thisOr(undo_magic_quotes($_REQUEST['filterer_auteur']), '');

