function connect_perso_seq_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('connect_perso_seq');
	if(!$arrPerm[1]){
		return false;
	}

	$data['perso'] = makeSafe($_REQUEST['perso']);
		if($data['perso'] == empty_lookup_value){ $data['perso'] = ''; }
	$data['seq'] = makeSafe($_REQUEST['seq']);
		if($data['seq'] == empty_lookup_value){ $data['seq'] = ''; }

	// hook: connect_perso_seq_before_insert
	if(function_exists('connect_perso_seq_before_insert')){
		$args=array();
		if(!connect_perso_seq_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `connect_perso_seq` set       `perso`=' . (($data['perso'] !== '' && $data['perso'] !== NULL) ? "'{$data['perso']}'" : 'NULL') . ', `seq`=' . (($data['seq'] !== '' && $data['seq'] !== NULL) ? "'{$data['seq']}'" : 'NULL'), $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"connect_perso_seq_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: connect_perso_seq_after_insert
	if(function_exists('connect_perso_seq_after_insert')){
		$res = sql("select * from `connect_perso_seq` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!connect_perso_seq_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	sql("insert ignore into membership_userrecords set tableName='connect_perso_seq', pkValue='" . makeSafe($recID, false) . "', memberID='" . makeSafe(getLoggedMemberID(), false) . "', dateAdded='" . time() . "', dateUpdated='" . time() . "', groupID='" . getLoggedGroupID() . "'", $eo);

	return $recID;
}

function connect_perso_seq_delete($selected_id, $AllowDeleteOfParents=false, $skipChecks=false){
	// insure referential integrity ...
	global $Translation;
	$selected_id=makeSafe($selected_id);

