	// hook: connect_perso_seq_before_update
	if(function_exists('connect_perso_seq_before_update')){
		$args=array();
		if(!connect_perso_seq_before_update($data, getMemberInfo(), $args)){ return false; }
	}

	$o=array('silentErrors' => true);
	sql('update `connect_perso_seq` set       `perso`=' . (($data['perso'] !== '' && $data['perso'] !== NULL) ? "'{$data['perso']}'" : 'NULL') . ', `seq`=' . (($data['seq'] !== '' && $data['seq'] !== NULL) ? "'{$data['seq']}'" : 'NULL') . " where `id`='".makeSafe($selected_id)."'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo '<a href="connect_perso_seq_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: connect_perso_seq_after_update
	if(function_exists('connect_perso_seq_after_update')){
		$res = sql("SELECT * FROM `connect_perso_seq` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
