		if(!connect_perso_seq_after_update($data, getMemberInfo(), $args)){ return; }
	}

	// mm: update ownership data
	sql("update membership_userrecords set dateUpdated='".time()."' where tableName='connect_perso_seq' and pkValue='".makeSafe($selected_id)."'", $eo);

}

