function personnages_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('personnages');
	if(!$arrPerm[1]){
		return false;
	}

	$data['nom'] = makeSafe($_REQUEST['nom']);
		if($data['nom'] == empty_lookup_value){ $data['nom'] = ''; }
	$data['type'] = makeSafe($_REQUEST['type']);
		if($data['type'] == empty_lookup_value){ $data['type'] = ''; }
	$data['description'] = makeSafe($_REQUEST['description']);
		if($data['description'] == empty_lookup_value){ $data['description'] = ''; }
	$data['quepossedetil'] = makeSafe($_REQUEST['quepossedetil']);
		if($data['quepossedetil'] == empty_lookup_value){ $data['quepossedetil'] = ''; }
	$data['apparait_quand'] = makeSafe($_REQUEST['apparait_quand']);
		if($data['apparait_quand'] == empty_lookup_value){ $data['apparait_quand'] = ''; }
	$data['disparait_quand'] = makeSafe($_REQUEST['disparait_quand']);
		if($data['disparait_quand'] == empty_lookup_value){ $data['disparait_quand'] = ''; }
	$data['creer_par'] = parseCode('<%%creatorUsername%%>', true);
	$data['rattache_a_dos2j'] = makeSafe($_REQUEST['rattache_a_dos2j']);
		if($data['rattache_a_dos2j'] == empty_lookup_value){ $data['rattache_a_dos2j'] = ''; }
	$data['rattache_a_seq'] = makeSafe($_REQUEST['rattache_a_seq']);
		if($data['rattache_a_seq'] == empty_lookup_value){ $data['rattache_a_seq'] = ''; }
	$data['illustration'] = PrepareUploadedFile('illustration', 102400,'jpg|jpeg|gif|png', false, '');
	if($data['illustration']) createThumbnail($data['illustration'], getThumbnailSpecs('personnages', 'illustration', 'tv'));
	if($data['illustration']) createThumbnail($data['illustration'], getThumbnailSpecs('personnages', 'illustration', 'dv'));
	if($data['nom']== ''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Nom': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	if($data['apparait_quand'] == '') $data['apparait_quand'] = "Il apparait quand...";
	if($data['disparait_quand'] == '') $data['disparait_quand'] = "Il disparait quand...";

	/* for empty upload fields, when saving a copy of an existing record, copy the original upload field */
	if($_REQUEST['SelectedID']){
		$res = sql("select * from personnages where id_perso='" . makeSafe($_REQUEST['SelectedID']) . "'", $eo);
		if($row = db_fetch_assoc($res)){
			if(!$data['illustration']) $data['illustration'] = makeSafe($row['illustration']);
		}
	}

	// hook: personnages_before_insert
	if(function_exists('personnages_before_insert')){
		$args=array();
		if(!personnages_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `personnages` set       `nom`=' . (($data['nom'] !== '' && $data['nom'] !== NULL) ? "'{$data['nom']}'" : 'NULL') . ', `type`=' . (($data['type'] !== '' && $data['type'] !== NULL) ? "'{$data['type']}'" : 'NULL') . ', ' . ($data['illustration'] != '' ? "`illustration`='{$data['illustration']}'" : '`illustration`=NULL') . ', `description`=' . (($data['description'] !== '' && $data['description'] !== NULL) ? "'{$data['description']}'" : 'NULL') . ', `quepossedetil`=' . (($data['quepossedetil'] !== '' && $data['quepossedetil'] !== NULL) ? "'{$data['quepossedetil']}'" : 'NULL') . ', `apparait_quand`=' . (($data['apparait_quand'] !== '' && $data['apparait_quand'] !== NULL) ? "'{$data['apparait_quand']}'" : 'NULL') . ', `disparait_quand`=' . (($data['disparait_quand'] !== '' && $data['disparait_quand'] !== NULL) ? "'{$data['disparait_quand']}'" : 'NULL') . ', `creer_par`=' . "'{$data['creer_par']}'" . ', `rattache_a_dos2j`=' . (($data['rattache_a_dos2j'] !== '' && $data['rattache_a_dos2j'] !== NULL) ? "'{$data['rattache_a_dos2j']}'" : 'NULL') . ', `rattache_a_seq`=' . (($data['rattache_a_seq'] !== '' && $data['rattache_a_seq'] !== NULL) ? "'{$data['rattache_a_seq']}'" : 'NULL'), $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"personnages_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: personnages_after_insert
	if(function_exists('personnages_after_insert')){
		$res = sql("select * from `personnages` where `id_perso`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!personnages_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	set_record_owner('personnages', $recID, getLoggedMemberID());

	return $recID;
}

function personnages_delete($selected_id, $AllowDeleteOfParents=false, $skipChecks=false){
	// insure referential integrity ...
	global $Translation;
	$selected_id=makeSafe($selected_id);

	// mm: can member delete record?
	$arrPerm=getTablePermissions('personnages');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='personnages' and pkValue='$selected_id'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='personnages' and pkValue='$selected_id'");
	if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3){ // allow delete?
		// delete allowed, so continue ...
	}else{
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: personnages_before_delete
	if(function_exists('personnages_before_delete')){
		$args=array();
		if(!personnages_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'];
	}

	// child table: lieu_et_scene
	$res = sql("select `id_perso` from `personnages` where `id_perso`='$selected_id'", $eo);
	$id_perso = db_fetch_row($res);
	$rires = sql("select count(1) from `lieu_et_scene` where `persos_present`='".addslashes($id_perso[0])."'", $eo);
	$rirow = db_fetch_row($rires);
	if($rirow[0] && !$AllowDeleteOfParents && !$skipChecks){
		$RetMsg = $Translation["couldn't delete"];
		$RetMsg = str_replace("<RelatedRecords>", $rirow[0], $RetMsg);
		$RetMsg = str_replace("<TableName>", "lieu_et_scene", $RetMsg);
		return $RetMsg;
	}elseif($rirow[0] && $AllowDeleteOfParents && !$skipChecks){
		$RetMsg = $Translation["confirm delete"];
		$RetMsg = str_replace("<RelatedRecords>", $rirow[0], $RetMsg);
		$RetMsg = str_replace("<TableName>", "lieu_et_scene", $RetMsg);
		$RetMsg = str_replace("<Delete>", "<input type=\"button\" class=\"button\" value=\"".$Translation['yes']."\" onClick=\"window.location='personnages_view.php?SelectedID=".urlencode($selected_id)."&delete_x=1&confirmed=1';\">", $RetMsg);
		$RetMsg = str_replace("<Cancel>", "<input type=\"button\" class=\"button\" value=\"".$Translation['no']."\" onClick=\"window.location='personnages_view.php?SelectedID=".urlencode($selected_id)."';\">", $RetMsg);
		return $RetMsg;
	}

	sql("delete from `personnages` where `id_perso`='$selected_id'", $eo);

	// hook: personnages_after_delete
	if(function_exists('personnages_after_delete')){
		$args=array();
		personnages_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("delete from membership_userrecords where tableName='personnages' and pkValue='$selected_id'", $eo);
}

