function redacteur_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('redacteur');
	if(!$arrPerm[1]){
		return false;
	}

	$data['member_id'] = parseCode('<%%creatorUsername%%>', true);
	$data['prenom'] = makeSafe($_REQUEST['prenom']);
		if($data['prenom'] == empty_lookup_value){ $data['prenom'] = ''; }
	$data['nom'] = makeSafe($_REQUEST['nom']);
		if($data['nom'] == empty_lookup_value){ $data['nom'] = ''; }
	$data['email'] = makeSafe($_REQUEST['email']);
		if($data['email'] == empty_lookup_value){ $data['email'] = ''; }
	$data['score_total'] = makeSafe($_REQUEST['score_total']);
		if($data['score_total'] == empty_lookup_value){ $data['score_total'] = ''; }
	$data['badges'] = makeSafe($_REQUEST['badges']);
		if($data['badges'] == empty_lookup_value){ $data['badges'] = ''; }
	$data['score_hist'] = makeSafe($_REQUEST['score_hist']);
		if($data['score_hist'] == empty_lookup_value){ $data['score_hist'] = ''; }
	$data['score_obj'] = makeSafe($_REQUEST['score_obj']);
		if($data['score_obj'] == empty_lookup_value){ $data['score_obj'] = ''; }
	$data['score_moy'] = makeSafe($_REQUEST['score_moy']);
		if($data['score_moy'] == empty_lookup_value){ $data['score_moy'] = ''; }
	$data['score_objets'] = makeSafe($_REQUEST['score_objets']);
		if($data['score_objets'] == empty_lookup_value){ $data['score_objets'] = ''; }
	$data['score_perso'] = makeSafe($_REQUEST['score_perso']);
		if($data['score_perso'] == empty_lookup_value){ $data['score_perso'] = ''; }
	$data['score_lets'] = makeSafe($_REQUEST['score_lets']);
		if($data['score_lets'] == empty_lookup_value){ $data['score_lets'] = ''; }
	if($data['score_total'] == '') $data['score_total'] = "0";
	if($data['score_hist'] == '') $data['score_hist'] = "0";
	if($data['score_obj'] == '') $data['score_obj'] = "0";
	if($data['score_moy'] == '') $data['score_moy'] = "0";
	if($data['score_objets'] == '') $data['score_objets'] = "0";
	if($data['score_perso'] == '') $data['score_perso'] = "0";
	if($data['score_lets'] == '') $data['score_lets'] = "0";

	// hook: redacteur_before_insert
	if(function_exists('redacteur_before_insert')){
		$args=array();
		if(!redacteur_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `redacteur` set       `member_id`=' . "'{$data['member_id']}'" . ', `prenom`=' . (($data['prenom'] !== '' && $data['prenom'] !== NULL) ? "'{$data['prenom']}'" : 'NULL') . ', `nom`=' . (($data['nom'] !== '' && $data['nom'] !== NULL) ? "'{$data['nom']}'" : 'NULL') . ', `email`=' . (($data['email'] !== '' && $data['email'] !== NULL) ? "'{$data['email']}'" : 'NULL') . ', `score_total`=' . (($data['score_total'] !== '' && $data['score_total'] !== NULL) ? "'{$data['score_total']}'" : 'NULL') . ', `badges`=' . (($data['badges'] !== '' && $data['badges'] !== NULL) ? "'{$data['badges']}'" : 'NULL'), $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"redacteur_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: redacteur_after_insert
	if(function_exists('redacteur_after_insert')){
		$res = sql("select * from `redacteur` where `id_redact`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!redacteur_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	set_record_owner('redacteur', $recID, getLoggedMemberID());

	return $recID;
}

function redacteur_delete($selected_id, $AllowDeleteOfParents=false, $skipChecks=false){
	// insure referential integrity ...
	global $Translation;
	$selected_id=makeSafe($selected_id);

	// mm: can member delete record?
	$arrPerm=getTablePermissions('redacteur');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='redacteur' and pkValue='$selected_id'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='redacteur' and pkValue='$selected_id'");
	if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3){ // allow delete?
		// delete allowed, so continue ...
	}else{
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: redacteur_before_delete
	if(function_exists('redacteur_before_delete')){
		$args=array();
		if(!redacteur_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'];
	}

	// child table: Dossier_histoire
	$res = sql("select `id_redact` from `redacteur` where `id_redact`='$selected_id'", $eo);
	$id_redact = db_fetch_row($res);
	$rires = sql("select count(1) from `Dossier_histoire` where `auteur`='".addslashes($id_redact[0])."'", $eo);
	$rirow = db_fetch_row($rires);
	if($rirow[0] && !$AllowDeleteOfParents && !$skipChecks){
		$RetMsg = $Translation["couldn't delete"];
		$RetMsg = str_replace("<RelatedRecords>", $rirow[0], $RetMsg);
		$RetMsg = str_replace("<TableName>", "Dossier_histoire", $RetMsg);
		return $RetMsg;
	}elseif($rirow[0] && $AllowDeleteOfParents && !$skipChecks){
		$RetMsg = $Translation["confirm delete"];
		$RetMsg = str_replace("<RelatedRecords>", $rirow[0], $RetMsg);
		$RetMsg = str_replace("<TableName>", "Dossier_histoire", $RetMsg);
		$RetMsg = str_replace("<Delete>", "<input type=\"button\" class=\"button\" value=\"".$Translation['yes']."\" onClick=\"window.location='redacteur_view.php?SelectedID=".urlencode($selected_id)."&delete_x=1&confirmed=1';\">", $RetMsg);
		$RetMsg = str_replace("<Cancel>", "<input type=\"button\" class=\"button\" value=\"".$Translation['no']."\" onClick=\"window.location='redacteur_view.php?SelectedID=".urlencode($selected_id)."';\">", $RetMsg);
		return $RetMsg;
	}

	sql("delete from `redacteur` where `id_redact`='$selected_id'", $eo);

	// hook: redacteur_after_delete
	if(function_exists('redacteur_after_delete')){
		$args=array();
		redacteur_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("delete from membership_userrecords where tableName='redacteur' and pkValue='$selected_id'", $eo);
}

