	$data['badges'] = makeSafe($_REQUEST['badges']);
		if($data['badges'] == empty_lookup_value){ $data['badges'] = ''; }
	$data['score_hist'] = makeSafe($_REQUEST['score_hist']);
		if($data['score_hist'] == empty_lookup_value){ $data['score_hist'] = ''; }
	$data['score_obj'] = makeSafe($_REQUEST['score_obj']);
		if($data['score_obj'] == empty_lookup_value){ $data['score_obj'] = ''; }
	$data['score_moy'] = makeSafe($_REQUEST['score_moy']);
		if($data['score_moy'] == empty_lookup_value){ $data['score_moy'] = ''; }
	$data['score_objets'] = makeSafe($_REQUEST['score_objets']);
		if($data['score_objets'] == empty_lookup_value){ $data['score_objets'] = ''; }
	$data['score_perso'] = makeSafe($_REQUEST['score_perso']);
		if($data['score_perso'] == empty_lookup_value){ $data['score_perso'] = ''; }
	$data['score_lets'] = makeSafe($_REQUEST['score_lets']);
		if($data['score_lets'] == empty_lookup_value){ $data['score_lets'] = ''; }
	$data['selectedID']=makeSafe($selected_id);

	// hook: redacteur_before_update
	if(function_exists('redacteur_before_update')){
		$args=array();
		if(!redacteur_before_update($data, getMemberInfo(), $args)){ return false; }
	}

	$o=array('silentErrors' => true);
	sql('update `redacteur` set       `prenom`=' . (($data['prenom'] !== '' && $data['prenom'] !== NULL) ? "'{$data['prenom']}'" : 'NULL') . ', `nom`=' . (($data['nom'] !== '' && $data['nom'] !== NULL) ? "'{$data['nom']}'" : 'NULL') . ', `email`=' . (($data['email'] !== '' && $data['email'] !== NULL) ? "'{$data['email']}'" : 'NULL') . ', `score_total`=' . (($data['score_total'] !== '' && $data['score_total'] !== NULL) ? "'{$data['score_total']}'" : 'NULL') . ', `badges`=' . (($data['badges'] !== '' && $data['badges'] !== NULL) ? "'{$data['badges']}'" : 'NULL') . " where `id_redact`='".makeSafe($selected_id)."'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo '<a href="redacteur_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: redacteur_after_update
	if(function_exists('redacteur_after_update')){
		$res = sql("SELECT * FROM `redacteur` WHERE `id_redact`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = $data['id_redact'];
		$args = array();
		if(!redacteur_after_update($data, getMemberInfo(), $args)){ return; }
	}

	// mm: update ownership data
	sql("update membership_userrecords set dateUpdated='".time()."' where tableName='redacteur' and pkValue='".makeSafe($selected_id)."'", $eo);

}

