	}
	$templateCode .= loadView('redacteur-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: redacteur_dv
	if(function_exists('redacteur_dv')){
		$args=array();
		redacteur_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
