function ref_badges_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('ref_badges');
	if(!$arrPerm[1]){
		return false;
	}

	$data['titre'] = makeSafe($_REQUEST['titre']);
		if($data['titre'] == empty_lookup_value){ $data['titre'] = ''; }
	$data['description'] = makeSafe($_REQUEST['description']);
		if($data['description'] == empty_lookup_value){ $data['description'] = ''; }
	$data['domaine'] = makeSafe($_REQUEST['domaine']);
		if($data['domaine'] == empty_lookup_value){ $data['domaine'] = ''; }
	$data['score_min'] = makeSafe($_REQUEST['score_min']);
		if($data['score_min'] == empty_lookup_value){ $data['score_min'] = ''; }
	$data['score_max'] = makeSafe($_REQUEST['score_max']);
		if($data['score_max'] == empty_lookup_value){ $data['score_max'] = ''; }
	$data['icone_badge'] = makeSafe($_REQUEST['icone_badge']);
		if($data['icone_badge'] == empty_lookup_value){ $data['icone_badge'] = ''; }
	if($data['titre']== ''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Titre': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}

	// hook: ref_badges_before_insert
	if(function_exists('ref_badges_before_insert')){
		$args=array();
		if(!ref_badges_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `ref_badges` set       `titre`=' . (($data['titre'] !== '' && $data['titre'] !== NULL) ? "'{$data['titre']}'" : 'NULL') . ', `description`=' . (($data['description'] !== '' && $data['description'] !== NULL) ? "'{$data['description']}'" : 'NULL') . ', `domaine`=' . (($data['domaine'] !== '' && $data['domaine'] !== NULL) ? "'{$data['domaine']}'" : 'NULL') . ', `score_min`=' . (($data['score_min'] !== '' && $data['score_min'] !== NULL) ? "'{$data['score_min']}'" : 'NULL') . ', `score_max`=' . (($data['score_max'] !== '' && $data['score_max'] !== NULL) ? "'{$data['score_max']}'" : 'NULL') . ', `icone_badge`=' . (($data['icone_badge'] !== '' && $data['icone_badge'] !== NULL) ? "'{$data['icone_badge']}'" : 'NULL'), $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"ref_badges_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: ref_badges_after_insert
	if(function_exists('ref_badges_after_insert')){
		$res = sql("select * from `ref_badges` where `id_badge`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!ref_badges_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	set_record_owner('ref_badges', $recID, getLoggedMemberID());

	return $recID;
}

function ref_badges_delete($selected_id, $AllowDeleteOfParents=false, $skipChecks=false){
	// insure referential integrity ...
	global $Translation;
	$selected_id=makeSafe($selected_id);

	// mm: can member delete record?
	$arrPerm=getTablePermissions('ref_badges');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='ref_badges' and pkValue='$selected_id'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='ref_badges' and pkValue='$selected_id'");
	if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3){ // allow delete?
		// delete allowed, so continue ...
	}else{
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: ref_badges_before_delete
	if(function_exists('ref_badges_before_delete')){
		$args=array();
		if(!ref_badges_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'];
	}

	// child table: redacteur
	$res = sql("select `id_badge` from `ref_badges` where `id_badge`='$selected_id'", $eo);
	$id_badge = db_fetch_row($res);
	$rires = sql("select count(1) from `redacteur` where `badges`='".addslashes($id_badge[0])."'", $eo);
	$rirow = db_fetch_row($rires);
	if($rirow[0] && !$AllowDeleteOfParents && !$skipChecks){
		$RetMsg = $Translation["couldn't delete"];
		$RetMsg = str_replace("<RelatedRecords>", $rirow[0], $RetMsg);
		$RetMsg = str_replace("<TableName>", "redacteur", $RetMsg);
		return $RetMsg;
	}elseif($rirow[0] && $AllowDeleteOfParents && !$skipChecks){
		$RetMsg = $Translation["confirm delete"];
		$RetMsg = str_replace("<RelatedRecords>", $rirow[0], $RetMsg);
		$RetMsg = str_replace("<TableName>", "redacteur", $RetMsg);
		$RetMsg = str_replace("<Delete>", "<input type=\"button\" class=\"button\" value=\"".$Translation['yes']."\" onClick=\"window.location='ref_badges_view.php?SelectedID=".urlencode($selected_id)."&delete_x=1&confirmed=1';\">", $RetMsg);
		$RetMsg = str_replace("<Cancel>", "<input type=\"button\" class=\"button\" value=\"".$Translation['no']."\" onClick=\"window.location='ref_badges_view.php?SelectedID=".urlencode($selected_id)."';\">", $RetMsg);
		return $RetMsg;
	}

	sql("delete from `ref_badges` where `id_badge`='$selected_id'", $eo);

	// hook: ref_badges_after_delete
	if(function_exists('ref_badges_after_delete')){
		$args=array();
		ref_badges_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("delete from membership_userrecords where tableName='ref_badges' and pkValue='$selected_id'", $eo);
}

function ref_badges_update($selected_id){
	global $Translation;

	// mm: can member edit record?
	$arrPerm=getTablePermissions('ref_badges');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='ref_badges' and pkValue='".makeSafe($selected_id)."'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='ref_badges' and pkValue='".makeSafe($selected_id)."'");
	if(($arrPerm[3]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[3]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[3]==3){ // allow update?
		// update allowed, so continue ...
	}else{
		return false;
	}

	$data['titre'] = makeSafe($_REQUEST['titre']);
		if($data['titre'] == empty_lookup_value){ $data['titre'] = ''; }
	if($data['titre']==''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Titre': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	$data['description'] = makeSafe($_REQUEST['description']);
		if($data['description'] == empty_lookup_value){ $data['description'] = ''; }
	$data['domaine'] = makeSafe($_REQUEST['domaine']);
		if($data['domaine'] == empty_lookup_value){ $data['domaine'] = ''; }
	$data['score_min'] = makeSafe($_REQUEST['score_min']);
		if($data['score_min'] == empty_lookup_value){ $data['score_min'] = ''; }
	$data['score_max'] = makeSafe($_REQUEST['score_max']);
		if($data['score_max'] == empty_lookup_value){ $data['score_max'] = ''; }
	$data['icone_badge'] = makeSafe($_REQUEST['icone_badge']);
		if($data['icone_badge'] == empty_lookup_value){ $data['icone_badge'] = ''; }
	$data['selectedID']=makeSafe($selected_id);

	// hook: ref_badges_before_update
	if(function_exists('ref_badges_before_update')){
		$args=array();
		if(!ref_badges_before_update($data, getMemberInfo(), $args)){ return false; }
	}

	$o=array('silentErrors' => true);
	sql('update `ref_badges` set       `titre`=' . (($data['titre'] !== '' && $data['titre'] !== NULL) ? "'{$data['titre']}'" : 'NULL') . ', `description`=' . (($data['description'] !== '' && $data['description'] !== NULL) ? "'{$data['description']}'" : 'NULL') . ', `domaine`=' . (($data['domaine'] !== '' && $data['domaine'] !== NULL) ? "'{$data['domaine']}'" : 'NULL') . ', `score_min`=' . (($data['score_min'] !== '' && $data['score_min'] !== NULL) ? "'{$data['score_min']}'" : 'NULL') . ', `score_max`=' . (($data['score_max'] !== '' && $data['score_max'] !== NULL) ? "'{$data['score_max']}'" : 'NULL') . ', `icone_badge`=' . (($data['icone_badge'] !== '' && $data['icone_badge'] !== NULL) ? "'{$data['icone_badge']}'" : 'NULL') . " where `id_badge`='".makeSafe($selected_id)."'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo '<a href="ref_badges_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: ref_badges_after_update
	if(function_exists('ref_badges_after_update')){
		$res = sql("SELECT * FROM `ref_badges` WHERE `id_badge`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = $data['id_badge'];
		$args = array();
		if(!ref_badges_after_update($data, getMemberInfo(), $args)){ return; }
	}

	// mm: update ownership data
	sql("update membership_userrecords set dateUpdated='".time()."' where tableName='ref_badges' and pkValue='".makeSafe($selected_id)."'", $eo);

}

