		if( $dvprint) $templateCode = str_replace('<%%VALUE(icone_badge)%%>', safe_html($urow['icone_badge']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(icone_badge)%%>', html_attr($row['icone_badge']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(icone_badge)%%>', urlencode($urow['icone_badge']), $templateCode);
	}else{
		$templateCode = str_replace('<%%VALUE(id_badge)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id_badge)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(titre)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(titre)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%HTMLAREA(description)%%>', '<textarea name="description" id="description" rows="5"></textarea>', $templateCode);
		$templateCode = str_replace('<%%VALUE(domaine)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(domaine)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(score_min)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(score_min)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(score_max)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(score_max)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(icone_badge)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(icone_badge)%%>', urlencode(''), $templateCode);
	}

	// process translations
	foreach($Translation as $symbol=>$trans){
		$templateCode = str_replace("<%%TRANSLATION($symbol)%%>", $trans, $templateCode);
	}

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == ''){
		$templateCode .= "\n\n<script>\$j(function(){\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption){
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id){
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('ref_badges');
	if($selected_id){
		$jdata = get_joined_record('ref_badges', $selected_id);
		if($jdata === false) $jdata = get_defaults('ref_badges');
		$rdata = $row;
	}
	$templateCode .= loadView('ref_badges-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: ref_badges_dv
	if(function_exists('ref_badges_dv')){
		$args=array();
		ref_badges_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
