function sequence_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('sequence');
	if(!$arrPerm[1]){
		return false;
	}

	$data['numero_ordre'] = makeSafe($_REQUEST['numero_ordre']);
		if($data['numero_ordre'] == empty_lookup_value){ $data['numero_ordre'] = ''; }
	$data['titre'] = makeSafe($_REQUEST['titre']);
		if($data['titre'] == empty_lookup_value){ $data['titre'] = ''; }
	$data['rattache_a_chapitre'] = makeSafe($_REQUEST['rattache_a_chapitre']);
		if($data['rattache_a_chapitre'] == empty_lookup_value){ $data['rattache_a_chapitre'] = ''; }
	$data['difficulte'] = makeSafe($_REQUEST['difficulte']);
		if($data['difficulte'] == empty_lookup_value){ $data['difficulte'] = ''; }
	$data['description'] = makeSafe($_REQUEST['description']);
		if($data['description'] == empty_lookup_value){ $data['description'] = ''; }
	$data['auteur'] = parseCode('<%%creatorUsername%%>', true);
	$data['modele'] = makeSafe($_REQUEST['modele']);
		if($data['modele'] == empty_lookup_value){ $data['modele'] = ''; }
	if($data['numero_ordre']== ''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Numero de la s&#233;quence': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	if($data['titre']== ''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Titre': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}

	// hook: sequence_before_insert
	if(function_exists('sequence_before_insert')){
		$args=array();
		if(!sequence_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `sequence` set       `numero_ordre`=' . (($data['numero_ordre'] !== '' && $data['numero_ordre'] !== NULL) ? "'{$data['numero_ordre']}'" : 'NULL') . ', `titre`=' . (($data['titre'] !== '' && $data['titre'] !== NULL) ? "'{$data['titre']}'" : 'NULL') . ', `difficulte`=' . (($data['difficulte'] !== '' && $data['difficulte'] !== NULL) ? "'{$data['difficulte']}'" : 'NULL') . ', `description`=' . (($data['description'] !== '' && $data['description'] !== NULL) ? "'{$data['description']}'" : 'NULL') . ', `auteur`=' . "'{$data['auteur']}'" . ', `modele`=' . (($data['modele'] !== '' && $data['modele'] !== NULL) ? "'{$data['modele']}'" : 'NULL'), $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"sequence_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// automatic rattache_a_chapitre
	if($_REQUEST['filterer_rattache_a_chapitre']){
		sql("update `sequence` set `rattache_a_chapitre`='" . makeSafe($_REQUEST['filterer_rattache_a_chapitre']) . "' where `id_sequence`='" . makeSafe($recID, false) . "'", $eo);
	}

	// hook: sequence_after_insert
	if(function_exists('sequence_after_insert')){
		$res = sql("select * from `sequence` where `id_sequence`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!sequence_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	sql("insert ignore into membership_userrecords set tableName='sequence', pkValue='" . makeSafe($recID, false) . "', memberID='" . makeSafe(getLoggedMemberID(), false) . "', dateAdded='" . time() . "', dateUpdated='" . time() . "', groupID='" . getLoggedGroupID() . "'", $eo);

	return $recID;
}

