function sequence_update($selected_id){
	global $Translation;

	// mm: can member edit record?
	$arrPerm=getTablePermissions('sequence');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='sequence' and pkValue='".makeSafe($selected_id)."'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='sequence' and pkValue='".makeSafe($selected_id)."'");
	if(($arrPerm[3]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[3]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[3]==3){ // allow update?
		// update allowed, so continue ...
	}else{
		return false;
	}

	$data['numero_ordre'] = makeSafe($_REQUEST['numero_ordre']);
		if($data['numero_ordre'] == empty_lookup_value){ $data['numero_ordre'] = ''; }
	if($data['numero_ordre']==''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Numero de la s&#233;quence': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	$data['titre'] = makeSafe($_REQUEST['titre']);
		if($data['titre'] == empty_lookup_value){ $data['titre'] = ''; }
	if($data['titre']==''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Titre': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	$data['rattache_a_chapitre'] = makeSafe($_REQUEST['rattache_a_chapitre']);
		if($data['rattache_a_chapitre'] == empty_lookup_value){ $data['rattache_a_chapitre'] = ''; }
	$data['difficulte'] = makeSafe($_REQUEST['difficulte']);
		if($data['difficulte'] == empty_lookup_value){ $data['difficulte'] = ''; }
	$data['description'] = makeSafe($_REQUEST['description']);
		if($data['description'] == empty_lookup_value){ $data['description'] = ''; }
	$data['modele'] = makeSafe($_REQUEST['modele']);
		if($data['modele'] == empty_lookup_value){ $data['modele'] = ''; }
	$data['selectedID']=makeSafe($selected_id);

	// hook: sequence_before_update
	if(function_exists('sequence_before_update')){
		$args=array();
		if(!sequence_before_update($data, getMemberInfo(), $args)){ return false; }
	}

	$o=array('silentErrors' => true);
	sql('update `sequence` set       `numero_ordre`=' . (($data['numero_ordre'] !== '' && $data['numero_ordre'] !== NULL) ? "'{$data['numero_ordre']}'" : 'NULL') . ', `titre`=' . (($data['titre'] !== '' && $data['titre'] !== NULL) ? "'{$data['titre']}'" : 'NULL') . ', `difficulte`=' . (($data['difficulte'] !== '' && $data['difficulte'] !== NULL) ? "'{$data['difficulte']}'" : 'NULL') . ', `description`=' . (($data['description'] !== '' && $data['description'] !== NULL) ? "'{$data['description']}'" : 'NULL') . ', `modele`=' . (($data['modele'] !== '' && $data['modele'] !== NULL) ? "'{$data['modele']}'" : 'NULL') . " where `id_sequence`='".makeSafe($selected_id)."'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo '<a href="sequence_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: sequence_after_update
	if(function_exists('sequence_after_update')){
		$res = sql("SELECT * FROM `sequence` WHERE `id_sequence`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = $data['id_sequence'];
		$args = array();
		if(!sequence_after_update($data, getMemberInfo(), $args)){ return; }
	}

	// mm: update ownership data
	sql("update membership_userrecords set dateUpdated='".time()."' where tableName='sequence' and pkValue='".makeSafe($selected_id)."'", $eo);

}

function sequence_form($selected_id = '', $AllowUpdate = 1, $AllowInsert = 1, $AllowDelete = 1, $ShowCancel = 0, $TemplateDV = '', $TemplateDVP = ''){
