		$combo_difficulte->ListData = $combo_difficulte->ListItem;
	}else{
		$combo_difficulte->ListItem = explode('||', entitiesToUTF8(convertLegacyOptions("introduction;;demo;;facile;;moyen;;difficile;;boss2fin")));
		$combo_difficulte->ListData = $combo_difficulte->ListItem;
	}
	$combo_difficulte->SelectName = 'difficulte';
